INSERT INTO races (race_id, title, stat_increase, special_traits, racial_spells, description) VALUES (1, 'Dragonborn', '+2 Strength & +1 Charisma', 'Draconic Ancestry  & Damage Resistance', 'Breath Weapon', 'Dragonborn look very much like dragons standing erect in humanoid form, though they lack wings or a tail.');
INSERT INTO races (race_id, title, stat_increase, special_traits, racial_spells, description) VALUES (2, 'Hill Dwarf', '+2 Constitution & +1 Wisdom', 'Dwarven Resilience & Dwarven Toughness & Dark Vision', 'none', 'Bold and hardy, dwarves are known as skilled warriors, miners, and workers of stone and metal. Hill Dwarves have keen senses, deep intuition, and remarkable resilience.');
INSERT INTO races (race_id, title, stat_increase, special_traits, racial_spells, description) VALUES (3, 'Mountain Dwarf', '+2 Constitution & +2 Strength', 'Dwarven Resilience & Dwarven Armor Training & Dark Vision', 'none', 'Bold and hardy, dwarves are known as skilled warriors, miners, and workers of stone and metal. Mountain Dwarves are strong and hardy, accustomed to a difficult life in rugged terrain.');
INSERT INTO races (race_id, title, stat_increase, special_traits, racial_spells, description) VALUES (3, 'High Elf', '+2 Dexterity & +1 Intelligence', 'Keen Senses & Fey Ancestry & Trance & Elf Weapon Training & Extra Language & Dark Vision', 'Cantrip', 'Elves are a magical people of otherworldly grace, living in the world but not entirely part of it.');